package com.tcr.springbootpoi.controller;import com.tcr.springbootpoi.service.ExportExcel;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ResponseBody;@Controllerpublic class ExportExcelController {    private ExportExcel<?> exportExcel;    @GetMapping("/download")    @ResponseBody    public static String downloadExcel(ExportExcelController exportExcelController) throws Exception {        exportExcelController.exportExcel = new ExportExcel<>();        exportExcelController.exportExcel.exportExcel();        return "下载成功!";    }}