package com.tcr.springbootpoi.controller;import com.tcr.springbootutils.helper.CommonMethod;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.text.SimpleDateFormat;import java.util.Date;@RestController@RequestMapping("/test")public class TestController {    /**     * 测试获取当前时间     */    @GetMapping("/date")    public String getDate(){        Date date = CommonMethod.getCurDate();        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        return sdf.format(date);    }}